from tqdm import tqdm
import time
def run():
    time.sleep(5)

chapters = ["first chap", "second chap", "third chap"]
for idx, chap in enumerate(tqdm(chapters, desc=f"Cleaning chap: "), start=1):
    print(f"cleaning {idx}")
    print("used")
    for _ in tqdm(range(5), desc="Waiting progress: "):
        time.sleep(1)
    print("Reseted")
    run()
    print("actual chap token")
    print("done cleaning")

